import React, { Component } from 'react'

export default class Message extends Component {
    state = {
        messageArr: [
            { id: '01', title: "消息1" },
            { id: '02', title: "消息2" },
            { id: '03', title: "消息3" }
        ]
    }
    render() {
        const { messageArr } = this.state
        return (
            <div>
                <ul>
                    {
                        messageArr.map((item) => {
                            return `<li>
                                        <a href="/message1">${item.title}</a>&nbsp;&nbsp;
                                    </li>`
                        })
                    }
                </ul>
            </div>
        )
    }
}
